package com.example.riderapp.adapter;import java.util.List;import android.app.Activity;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.ImageView;import android.widget.TextView;import com.example.riderapp.R;import com.example.riderapp.activity.LoginActivity;import com.example.riderapp.model.Artical;import com.squareup.picasso.Picasso;public class ArticalsAdapter extends ArrayAdapter<Artical> {	private Context context;	private List<Artical> objects;	public ArticalsAdapter(Context context, int resource, List<Artical> objects) {		super(context, resource, objects);		this.context = context;		this.objects = objects;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		Artical artical = objects.get(position);		LayoutInflater inflater = (LayoutInflater) context				.getSystemService(Activity.LAYOUT_INFLATER_SERVICE);		View view = inflater.inflate(R.layout.artical_item_layout, null);		TextView arti_title = (TextView) view.findViewById(R.id.artical_title);		TextView from_where = (TextView) view.findViewById(R.id.from_where);		TextView to_where = (TextView) view.findViewById(R.id.to_where);		TextView leader = (TextView) view.findViewById(R.id.leader);		ImageView photo = (ImageView) view.findViewById(R.id.arti_photo);		Picasso.with(context).load("http://pic16.nipic.com/20110823/6081234_144245260084_2.jpg")				.into(photo);				from_where.setText(artical.getFromWhere());		to_where.setText(artical.getToWhere());		leader.setText(artical.getLeader());		arti_title.setText(artical.getTitle());		return view;	}}