package com.example.riderapp.adapter;import java.util.List;import android.app.Activity;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.TextView;import com.example.riderapp.R;import com.example.riderapp.model.Artical;import com.example.riderapp.model.Teamate;public class TeamateListAdapter extends ArrayAdapter<Teamate> {	List<Teamate> teamates;	Context context;	public TeamateListAdapter(Context context, int resource,			List<Teamate> teamates) {		super(context, resource,teamates);		this.context = context;		this.teamates = teamates;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		TextView tv_membername = null;		Teamate teamate = teamates.get(position);		LayoutInflater inflater = (LayoutInflater) context				.getSystemService(Activity.LAYOUT_INFLATER_SERVICE);		View view = inflater.inflate(R.layout.teamate_list_item, null);		tv_membername = (TextView) view.findViewById(R.id.team_teamate_name);		tv_membername.setText(teamate.getName());		return view;	}}