package com.example.riderapp.data;import android.content.Context;import android.content.SharedPreferences;import com.example.riderapp.model.RideActivity;import com.example.riderapp.webservice.WsClient;public class ActivityDataSource {	private WsClient wsClient;	private SharedPreferences preferences;	private static final String PREFKEY = "riderapp";	private Context context;	public ActivityDataSource() {	}	public ActivityDataSource(Context context) {		preferences = context.getSharedPreferences(PREFKEY,				Context.MODE_PRIVATE);		this.context = context;		wsClient = new WsClient(AppConfig.WSDL, AppConfig.WSDL_NAMESPACE);	}	public Object addActivity(RideActivity ra) {		Object result = wsClient.getSoapObject("addRaActivity", "title",				ra.getTitle(), "leader",				new UserDataSource(context).client.getId() + "", "cdate",				System.currentTimeMillis() + "", "bdate", ra.getBegin_date()						+ "", "desc", ra.getDesc(), "fromWhere",				ra.getFrom_where(), "toWhere", ra.getTo_where(), "x",				ra.getLocation_X() + "", "y", ra.getLocation_Y() + "");		return result;	}}