package com.example.riderapp.data;import java.util.ArrayList;import java.util.List;import com.example.riderapp.model.Teamate;public class LocationShareDataSource {	List<Teamate> teamates = new ArrayList<>();	public LocationShareDataSource() {		for (int i = 0; i < 4; i++) {			Teamate tm = new Teamate();			tm.setName("小晓鹏");			tm.setLatitude(23.195889 + 0.01 * i);			tm.setLongitude(113.364701 + 0.01 * i);			teamates.add(tm);		}	}	public List<Teamate> getAll() {		return teamates;	}}