package com.example.testlayout;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.widget.EditText;public class EditorMain extends Activity{	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);						setContentView(R.layout.editor_layout);		Intent intent = this.getIntent();				String text = intent.getStringExtra("text");						EditText et= (EditText)findViewById(R.id.editText1);				et.setText(text);							}}